import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchEvents } from '../Redux/calendarActions';
import '../Assets/Css/Calendar.css';

const Calendar = () => {
  const dispatch = useDispatch();
  const events = useSelector((state) => state.calendar.events);

  useEffect(() => {
    dispatch(fetchEvents());
  }, [dispatch]);

  // Function to render events for a specific date
  const renderEvents = (date) => {
    const dateKey = date.toISOString().split('T')[0];
    const dateEvents = events[dateKey] || [];

    return dateEvents.map((event, index) => (
      <div key={index} className="event">
        <div className="event-title">{event.title}</div>
        <div className="event-time">{event.time}</div>
      </div>
    ));
  };

  // Function to render a single date cell
  const renderDateCell = (date) => {
    return (
      <div key={date.toISOString()} className="date-cell">
        <div className="date">{date.getDate()}</div>
        {renderEvents(date)}
      </div>
    );
  };

  // Function to render a week
  const renderWeek = (startDate, hasEvents) => {
    const week = [];

    for (let i = 0; i < 7; i++) {
      const currentDate = new Date(startDate);
      currentDate.setDate(startDate.getDate() + i);
      week.push(renderDateCell(currentDate));
    }

    return (
      <div
        key={startDate.toISOString()}
        className={`week ${hasEvents ? 'has-events' : ''}`}
        onClick={() => handleWeekClick(startDate)}
      >
        {week}
      </div>
    );
  };

  // Function to render the entire month
  const renderMonth = () => {
    const currentDate = new Date();
    const firstDayOfMonth = new Date(currentDate.getFullYear(), currentDate.getMonth(), 1);
    const lastDayOfMonth = new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 0);
    const weeks = [];
    let startDate = new Date(firstDayOfMonth);

    while (startDate <= lastDayOfMonth) {
      const dateKey = startDate.toISOString().split('T')[0];
      const hasEvents = events[dateKey] && events[dateKey].length > 0;
      weeks.push(renderWeek(startDate, hasEvents));
      startDate.setDate(startDate.getDate() + 7);
    }

    // Remove any extra days in the last row
    while (weeks[weeks.length - 1].length < 7) {
      weeks[weeks.length - 1].push(null);
    }

    return weeks;
  };

  // State to manage the selected week's event details
  const [selectedWeekEvents, setSelectedWeekEvents] = useState([]);

  // Function to handle week click and display event details
  const handleWeekClick = (startDate) => {
    const dateKey = startDate.toISOString().split('T')[0];
    setSelectedWeekEvents(events[dateKey] || []);
  };

  return (
    <div className="calendar">
        <h1>Calendar</h1>
      <div className="header">August 2023</div>
      <div className="days-of-week">
        <div>Sun</div>
        <div>Mon</div>
        <div>Tue</div>
        <div>Wed</div>
        <div>Thu</div>
        <div>Fri</div>
        <div>Sat</div>
      </div>
      {renderMonth()}
      <div className="event-details">
        {selectedWeekEvents.map((event, index) => (
          <div key={index} className="event-detail">
            <div className="event-title">{event.title}</div>
            <div className="event-time">{event.time}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Calendar;
